import { useState, useEffect } from "react";

import closeImg from '../../assets/close.svg';

import { Container } from "./styles";

import api from '../../Services/api'

interface IPositions{
  id: string;
  description: string;
  cbo: string;
  salary: number;
  labor: string;
  occupation: string;
}

export function DisciplinasTable() {
  const [position, setFaculdade] = useState<IPositions[]>([])

  useEffect(() => {
    api.get('position').then(response => setFaculdade(response.data))
  }, [position] )

  async function onDelete(id: string){
    await api.delete(`/position/${id}`)
    setFaculdade(position.filter(facul => facul.id !== id))
  }

  return (
    <Container>
      <table>
        <thead>
          <tr>
            <th>Descrição</th>
            <th>CBO</th>
            <th>Tipo de Mão de Obra</th>
            <th>Função</th>
            <th>Salaário</th>
            <th></th>
          </tr>
        </thead>

        <tbody>

            {position.map(pos => (
              <tr>
                <td>{pos.disciplina}</td>
                <td>{pos.cargahoraria}</td>
                <td>{pos.professor}</td>
                <td>
                  <button type="button" onClick={() => onDelete(facul.id)}>
                    <img src={closeImg} alt="Excluir Matéria" />
                  </button>
                </td>
              </tr>
            ))}
        </tbody>

      </table>
    </Container>
  )
}
