import {useState, FormEvent} from 'react'
import Modal from 'react-modal'

import closeImg from '../../assets/close.svg'

import { Container, TiposDeMaoDeObra } from './styles'

import api from '../../Services/api'


interface NewInclusionModalProps{
    isOpen: boolean;
    onRequestClose: () => void;
}

export function NewEventsModal({ isOpen, onRequestClose }: NewInclusionModalProps ){
  const [description, setDescription] = useState('')
  const [cbo, setCBO] = useState('')
  const [salary, setSalary] = useState(0)


  async function handleCreateDisciplina(event: FormEvent){
    event.preventDefault();
    const data = {
      description,
      cbo,
      salary
    }

    await api.post('/faculdade', data)

    setDescription('')
    setCBO('')
    setSalary(0)
  }

    return(
      <Modal isOpen={isOpen} onRequestClose={onRequestClose} overlayClassName="reactModalOverlay" className="reactModalContent">        

          <button type="button" onClick={onRequestClose} className="reactModalClose">
            <img src={closeImg} alt="Fechar Modal" />
          </button>

          <Container onSubmit = {handleCreateDisciplina}>
            <h2>Cadastrar Disciplina</h2>

            <input placeholder="Descrição" type="text" value = {description} onChange = {event => setDescription(event.target.value)}/>
            <input placeholder="CBO" type="text" value = {cbo} onChange = {event => setCBO(event.target.value)}/>
            <input placeholder="Salário" type="number" value = {salary} onChange = {event => setSalary(Number(event.target.value))}/>            
            {/* <input placeholder="Salário" type="number" value={salario} onChange = {event => setSalario(Number(event.target.value))}></input> */}
            <h3>Mão de Obra</h3>
            <TiposDeMaoDeObra>
              <button type='button'>
                <span>Direta</span>
              </button>
              <button type='button'>
                <span>Indireta</span>
              </button>
            </TiposDeMaoDeObra>


            <button type="submit">Confirmar</button>
          </Container>

      </Modal>
    )
}
