import styled from "styled-components";

export const Container = styled.form`
    h2{
        color: var(--text-tilte);
        fount-size: 1.5rem;
        margin-bottom: 2rem;
    }

    input{
        width: 100%;
        padding: 0 1.5rem;
        height: 4rem;
        border-radius: 0.50rem;

        border: 1px solid #d7d7
        background: #e7e9ee;

        font
    }
`
